<script>
import '~/assets/scss/global.scss'
export default {
  name: 'Base',

  data() {
    return {
      show_nav: false,
    }
  },

  computed: {
    user_status() {
      return this.$store.getters['user_data/user_status']
    },
    avatar() {
      return this.$store.getters['user_data/user_avatar']
    },
  },

  created() {
    this.$store.dispatch('auth/getInfo')
  },

  methods: {
    toggleNav() {
      this.show_nav = !this.show_nav
    },
  },
}
</script>

<style module lang="scss">
@import '~/assets/scss/variables.scss';
@import '~/assets/scss/global.scss';

.avatar,
.burger-icon,
.nav-mobile {
  display: none;
}

.base {
  display: flex;
  min-height: 100vh;
  background: radial-gradient(
    47.39% 118.47% at 3.68% -21.35%,
    rgba(255, 219, 19, 0.1) 0%,
    rgba(255, 125, 75, 0.1) 30.4%,
    rgba(231, 130, 247, 0.1) 62.15%,
    rgba(216, 65, 137, 0.1) 100%
  );
  padding-top: 30px;
  padding-right: 20px;
}

.sidebar {
  width: 17%;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-left: 20px;
  padding-right: 18px;
  box-sizing: border-box;
}

.logo {
  width: 100%;
  margin-bottom: 40px;
}

.nav,
.links {
  width: 100%;
}

.links {
  list-style-type: none;
  padding-left: 0;

  li {
    width: 100%;
    height: 50px;
    border-radius: 16px;
    background-color: none;
    transition: background-color 0.3s ease;
    margin-bottom: 23px;
    display: flex;
    align-items: center;

    svg {
      display: block;
      margin-right: 25px;
    }

    &:hover {
      background-color: $baseOrange;

      svg,
      path {
        fill: #fff;
      }

      a {
        color: #fff;
      }
    }

    a {
      display: flex;
      align-items: center;
      width: 100%;
      height: 100%;
      color: $baseBlack;
      font-weight: 600;
      font-size: 13px;
      padding-left: 25px;
    }
  }
}

.main {
  flex-grow: 1;
  width: 87%;
  background: $baseBg;
  border: 6px solid rgba(39, 41, 49, 0.07);
  border-radius: 20px 20px 0 0;
  border-bottom: none;
  padding: 30px;
  box-sizing: border-box;
}

@media (max-width: $smallDesktop) {
  .sidebar {
    padding: 0 5px;
  }

  .links {
    li a {
      padding-left: 5px;
    }
  }
}

@media (max-width: $mobile) {
  .sidebar {
    width: 100%;
    padding: 0px 15px;
    flex-direction: row;
    justify-content: space-between;
  }

  .nav-mobile {
    display: block;
  }

  .base {
    flex-direction: column;
    padding-right: 0;
  }

  .logo {
    width: 136px;
    height: 44px;
    margin-bottom: 0;
  }

  .nav-mobile {
    position: relative;
    left: 155px;
    top: -50px;
  }

  .nav {
    position: fixed;
    transition: 0.2s ease;
    background-color: #fff;
    height: 100vh;
    top: 0;
    z-index: 3;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 0px 10px;
  }

  .nav-out {
    left: -100%;
  }

  .nav-in {
    left: 0;
  }

  .burger-icon {
    width: 20px;
    height: 16px;
  }

  .avatar {
    width: 40px;
    height: 40px;
    border-radius: 36px;
    object-fit: cover;
  }

  .avatar,
  .burger-icon {
    display: block;
  }

  .main {
    width: 100%;
    margin-top: 28px;
    position: relative;
    overflow: hidden;
    padding: 16px 16px;
  }
}
</style>
